--
-- Table structure for table `paints`
--

CREATE TABLE IF NOT EXISTS `paints` (
`id` int(11) NOT NULL,
  `name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `author` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `date` int(11) DEFAULT NULL,
  `intro` text COLLATE utf8_unicode_ci,
  `image` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=39 ;

--
-- Dumping data for table `paints`
--

INSERT INTO `paints` (`id`, `name`, `author`, `date`, `intro`, `image`, `created_at`, `updated_at`) VALUES
(5, 'The Birth of Venus (Sự ra đời của thần Vệ Nữ)', 'Sandro Botticelli (Italy)', 2014, 'Sandro Botticelli là một họa sĩ thiên tài người Italy vào thời kỳ tiền Phục Hưng. The Birth of Venus là tác phẩm vĩ đại nhất của ông và được coi như là một biểu tượng của “Chân - Thiện - Mỹ”. Trong thần thoại Hy Lạp, thần Vệ Nữ được sinh ra từ bọt biển, là con của Chúa tể bầu trời - thần Uranus. Sự ra đời của thần Vệ Nữ trở thành một đề tài lớn trong hội họa, trong đó có những bức tranh rất nổi tiếng của Cabanel, Bouguereau hay Amaury Duval.\r\n\r\nTuy vậy, bức tranh của Botticelli vẫn được công nhận rộng rãi là tác phẩm hoàn hảo và kinh điển nhất. Trong tranh, thần Vệ Nữ được miêu tả có mái tóc mây vàng rực rỡ, làn da trắng muốt, gương mặt thánh thiện và làn môi mọng. Vệ Nữ có cơ thể tròn trịa, đầy đặn, tràn đầy sức sống, tượng trưng cho sự sinh sôi, tình yêu, hoan lạc và sắc đẹp.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092132381_17229368.jpg', '2014-12-04 09:06:59', '2014-12-04 09:12:15'),
(6, 'Mona Lisa (Nàng Mona Lisa)', 'Leonardo da Vinci (Italy)', 1056, 'Leonardo da Vinci là một thiên tài toàn năng người Italy: một họa sĩ, kiến trúc sư kiệt xuất, một nhạc sĩ, một kỹ sư siêu việt, một nhà khoa học đi trước thời đại…Mona Lisa, không nghi ngờ gì, là tác phẩm hội họa nổi tiếng nhất thế giới, được coi như chuẩn mực của cái đẹp.\r\n\r\nBức họa thể hiện một người đàn bà đầy đặn, trông có vẻ phúc hậu đang đan tay vào nhau, khóe miệng khẽ giãn ra một nụ cười hư ảo. Hậu thế đã cố gắng phân tích bí ẩn trong nụ cười của nàng Mona Lisa: nàng đang cười hay không cười, nụ cười đó có ý nghĩa gì, làm sao da Vinci có thể tạo nên một nụ cười như vậy… nhưng chưa bao giờ đưa ra được câu trả lời thỏa mãn. Đã có hàng loạt giả thuyết, huyền thoại xung quanh nguyên mẫu của bức tranh.\r\n\r\nNgười ta tranh cãi nguyên mẫu của bức tranh là mẹ của họa sĩ, một gái điếm, vợ của một thương gia giàu có, người tình đồng giới của ông hoặc là chính Leonardo da Vinci. Đáp lại sự tò mò của hậu thế, Mona Lisa vẫn mãi mãi mỉm cười bí ẩn, như thể luôn muốn nói một điều gì mà chẳng bao giờ nói.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092132222_17229507.jpg', '2014-12-04 15:01:33', '2014-12-04 15:01:33'),
(7, 'Sleeping Venus (Vệ Nữ say ngủ)', 'Giorgione (Italy)', 1510, 'Giorgione là một bậc thầy hội họa của phong trào Phục Hưng. Cuộc đời của ông ngắn ngủi, để lại không nhiều tác phẩm và trong số đó, Sleeping Venus là bức tranh nổi tiếng nhất. Khi Giorgione mất, bức Sleeping Venus vẫn còn dang dở và Titian, một danh họa vĩ đại khác, cũng là bạn của ông đã hoàn thành bức tranh bằng cách vẽ thêm phong cảnh và bầu trời.\r\n\r\nSleeping Venus vẽ một phụ nữ nằm nghiêng, mắt nhắm nghiền như đang ngủ, mặt khẽ quay về hướng người xem, tay phải làm gối, tay trái đặt hờ lên cơ thể, chân trái duỗi đặt trên chân phải hơi co lại – một kiểu bố cục kinh điển mà rất nhiều tác phẩm hội họa về sau đã học theo. Thiên nhiên đằng sau nàng cũng có những đường cong tinh tế như họa lại cơ thể của người đàn bà, tôn vinh sự hòa hợp giữa con người và thiên nhiên. Việc vẽ một người phụ nữ khỏa thân đánh dấu một cuộc cách mạng trong nghệ thuật và được coi là một trong những điểm khởi đầu của mỹ thuật hiện đại.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092132504_17229583.jpg', '2014-12-04 15:05:13', '2014-12-04 15:05:13'),
(8, 'Girl with a Pearl Earring (Thiếu nữ đeo hoa tai ngọc trai)', 'Johannes Vermeer (Hà Lan)', 1665, 'Johannes Vermeer là một trong những họa sĩ vĩ đại nhất của Thời đại Hoàng kim Hà Lan. Sinh thời, ông vẽ rất chậm và để lại rất ít tranh, phần lớn tranh của ông lấy đề tài những người phụ nữ của tầng lớp bình dân. Girl with a Pearl Earring là một trong những kiệt tác để đời của ông, được mệnh danh là “Mona Lisa Hà Lan” hoặc “Mona Lisa phương Bắc”.\r\n\r\nBức tranh mô tả một thiếu nữ nhìn nghiêng, khăn đội đầu che hết mái tóc, đồ trang sức duy nhất là một bên hoa tai bằng ngọc trai. Thiếu nữ trong tranh có đôi mắt tròn to, lộ vẻ rụt rè, sợ hãi nhưng làn môi mọng lại hé mở, khơi gợi ham muốn. Bức tranh vừa thể hiện sự ngây thơ mong manh, vừa phảng phất một sự cám dỗ gọi mời. Ánh sáng phản chiếu trên hoa tai ngọc trai là một điểm nhấn hoàn hảo của tác phẩm. Năm 1999, Tracy Chevalier đã viết một cuốn tiểu thuyết lịch sử nổi tiếng cùng tên với cảm hứng dựa trên bức tranh này.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092132048_17229170.jpg', '2014-12-04 15:06:55', '2014-12-04 15:06:55'),
(9, 'Whistler’s Mother (Mẹ của Whistler)', 'James McNeil Whistler (Mỹ)', 1871, 'Whistler là một họa sĩ nổi tiếng gốc Mỹ, một trong những chủ soái của phong trào “nghệ thuật vị nghệ thuật”. Whistler’s Mother là bức tranh nổi tiếng nhất của ông, được coi là biểu tượng Mỹ và “Mona Lisa của thời Victoria”.\r\n\r\nTương truyền, Whistler đã định vẽ một người khác nhưng bất thành và ông liền thay thế người mẫu bằng chính mẹ ông, bà Anna Whistler. Cũng có tin đồn, đầu tiên bà Whistler được vẽ đứng nhưng sau đó vì quá mỏi chân, bà phải ngồi trên ghế.\r\n\r\nTrong tranh, bà mặc một bộ váy màu đen giản dị, ngồi gọn gàng, tay đặt trên váy, chân đặt trên chiếc ghế để chân, nhìn chăm chú về phía trước. Nét mặt nghiêm trang và tông màu đen xám của bức tranh gợi một cảm giác ngột ngạt và tang tóc như thể một điều gì đó rất nghiêm trọng đang xảy ra. Tuy nhiên, ngày nay bức tranh được rộng rãi công nhận như là một biểu tượng mẫu mực của tình mẹ.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092342471_17229576.jpg', '2014-12-04 15:09:57', '2014-12-04 15:09:57'),
(10, 'Portrait of an Unknown Woman (Người đàn bà xa lạ)', 'Ivan Nikolaevich Kramskoi (Nga)', 1833, 'Kramskoi là một họa sĩ và nhà phê bình nghệ thuật nổi tiếng, chủ soái của phong trào nghệ thuật dân chủ Nga. Portrait of an Unknown Woman được coi là một kiệt tác của nền hội họa Nga và từ lâu đã rất quen thuộc với công chúng Việt Nam với tên gọi Người đàn bà xa lạ.\r\n\r\nBức tranh vẽ một người phụ nữ ngồi trên xe ngựa mui trần vào buổi sáng mùa đông ảm đạm, tuyết phủ trắng xóa mái nhà. Người phụ nữ trong tranh quý phái và kiêu hãnh với đầu ngẩng cao, môi mím chặt nghiêm nghị, ánh mắt nhìn xuống uể oải.\r\n\r\nCho đến nay, nguyên mẫu của bức họa vẫn là một điều bí ẩn với rất nhiều phỏng đoán chưa được chứng minh. Bức tranh thường được ví với Anna Karenina trong kiệt tác cùng tên của đại văn hào Leo Tolstoy và đã nhiều lần được dùng để minh họa cho trang bìa của cuốn sách. Bức tranh được đánh giá là thể hiện tính cách Nga rõ rệt và là một biểu tượng của sự đài các, thượng lưu.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092301969_17229820.jpg', '2014-12-04 15:11:09', '2014-12-04 15:11:09'),
(11, 'Madame X (Quý bà X)', 'John Singer Sargent (Mỹ)', 1884, 'John Singer Sargent là một họa sĩ vẽ tranh chân dung hàng đầu thế kỷ 19. Madame X là bức tranh nổi tiếng và có lẽ là tai tiếng nhất của ông.\r\n\r\nNgười phụ nữ trong tranh là Virginie Gautreau, vợ của một ông chủ nhà băng giàu có người Pháp. Sinh thời, bà rất nổi tiếng trong giới thượng lưu Paris vì sắc đẹp ưu việt của mình. Bức chân dung vẽ bà trong bộ váy màu đen cúp ngực, tay phải làm trụ khẽ đỡ lấy cơ thể, tay khác bấu hờ vạt váy và gương mặt quay đi hướng khác. Màu đen bóng của bộ váy cùng tông màu nâu của hậu cảnh như tương phản và càng làm nổi bật thêm làn da trắng nhức.\r\n\r\nCơ thể người mẫu như hướng về phía người họa sĩ nhưng đầu lại quay sang một bên, vẻ lảng tránh, tạo cảm giác xung đột, vừa muốn tiến đến vừa muốn cưỡng lại. Bức tranh bị coi là khêu gợi, không đứng đắn và trở thành một "quả bom" scandal thời bấy giờ. Bà Gautreau từ đó không xuất hiện trước công chúng nữa còn họa sĩ Sargent thì nhanh chóng rời khỏi Pháp, định cư lâu dài ở London.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092301844_17229122.jpg', '2014-12-04 15:12:42', '2014-12-04 15:12:42'),
(12, 'Flaming June (Tháng Sáu bỏng cháy)', 'Nam tước Frederic Leighton (Anh)', 1895, 'Nam tước Frederic Leighton là một họa sĩ kiêm nhà điêu khắc nổi tiếng người Anh.Flaming June là tác phẩm tiêu biểu nhất của ông và được coi là một kiệt tác của hội họa thế giới.\r\n\r\nNguyên mẫu trong tranh là nữ diễn viên Dorothy Dene, người được đánh giá là có gương mặt và hình thể giống với hình mẫu lý tưởng của Leighton nhất. Dorothy Dene vào những năm 1890 cũng được mệnh danh là người đẹp nhất nước Anh. Bức tranh khắc họa cô đang nằm ngủ trong một tư thế thiếu tự nhiên tựa như đó chỉ là một giấc ngủ ngắn mệt nhọc.\r\n\r\nTác phẩm đẫm chìm trong màu hổ phách hoàn hảo và cảnh dòng sông ở hậu cảnh gần như sáng trắng dưới ánh nắng mặt trời tháng sáu. Bộ váy mỏng manh trong suốt thể hiện những đường cong tinh tế của người phụ nữ. Cây trúc đào bên góc phải tượng trưng cho sự mong manh giữa giấc ngủ và cái chết. Thiếu nữ trong tranh với vẻ đẹp vừa nhục dục vừa trong sáng khiến người xem nhớ đến những tiên nữ nanh-phơ trong thần thoại Hy Lạp ngày xưa.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092471855_17229780.jpg', '2014-12-04 15:14:02', '2014-12-04 15:14:02'),
(13, 'Portrait of Adele Bloch-Bauer I (Chân dung nàng Adele Bloch-Bauer I)', 'Gustav Klimt (Áo)', 1907, 'Gustav Klimt là một họa sĩ thiên tài người Áo, nổi tiếng với những đề tài về phụ nữ và tình yêu. Cho tới nay, Portrait of Adele Bloch-Bauer I là một trong 5 bức tranh được trả giá cao nhất trong lịch sử. Năm 2006, bức tranh được bán với giá kỷ lục 135 triệu USD, trở thành bức tranh đắt giá nhất lúc bấy giờ.\r\n\r\nAdele Bloch-Bauer là vợ của một nhà tư bản kếch xù, người thường mạnh dạn ủng hộ và giúp đỡ tiền bạc cho Gustav Klimt. Bà là tình nhân bí mật của Klimt và cũng là người phụ nữ duy nhất được họa sĩ hai lần vẽ chân dung. Bức chân dung thứ hai của bà, Portrait of Adele Bloch-Bauer II, từng được trả giá 88 triệu USD, cũng là một trong những bức tranh đắt giá nhất thế giới.\r\n\r\nPortrait of Adele Bloch-Bauer I tràn ngập một màu vàng vương giả, người phụ nữ trong tranh trông thoải mái và đường bệ như một nữ hoàng. Bức tranh còn được mệnh danh là “Mona Lisa của nước Áo” hoặc “Phu nhân vàng”.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092471980_172210835.jpg', '2014-12-04 15:15:06', '2014-12-04 15:15:06'),
(14, 'Dora Maar with Cat (Dora Maar với Mèo)', 'Pablo Picasso (Tây Ban Nha)', NULL, 'Picasso là họa sĩ thiên tài người Tây Ban Nha, một trong những cái tên sáng chói nhất trong giới hội họa thế kỷ 20. Dora Maar with Cat được giới phê bình cùng công chúng đánh giá cao và là một trong những bức tranh đắt nhất thế giới với giá hơn 95 triệu USD.\r\n\r\nDora Maar là người tình lâu năm của họa sĩ và cũng là một nhiếp ảnh, họa sĩ có tiếng. Trong tranh, bà ngồi trên một cái ghế trông như ngai vàng với một cái mũ trông như vương miện trên đầu. Một con mèo nhỏ xuất hiện bên vai phải, trông vừa có sự hăm dọa, vừa có vẻ hài hước. Những móng tay của Dora Maar cũng được vẽ rất dài và sắc nhọn, hàm ý cô cũng là một con mèo nhỏ ranh mãnh. Bức tranh cũng rất nổi tiếng về sự lựa chọn màu sắc, phối màu phức tạp.', '10-buc-ha-noi-tieng-ve-phu-nu-1382092471692_17221017.jpg', '2014-12-04 15:16:09', '2014-12-04 15:26:50'),
(15, 'The Starry Night', 'Vincent van Gogh', 1889, 'Tháng 9 năm 1889, họa sĩ người Hà Lan Van Gogh gửi một bưu kiện cho em trai mình là Theo, trong bưu kiện chính là bức họa The Starry Night. Về sau, đây là một trong vài họa phẩm đắt giá nhất của Van Gogh.\r\n\r\n"The Starry Night" hay còn gọi là Đêm đầy sao, là bức sơn dầu được vẽ trong thời gian họa sĩ người Hà Lan Van Gogh lưu trú tại viện tâm thần Saint-Rémy-de-Provence (Pháp). \r\n\r\nBức tranh vẽ vào ban ngày, qua trí nhớ, miêu tả khung cảnh bên ngoài cửa sổ phòng bệnh của ông. Nhiều chuyên gia cho rằng tranh Van Gogh có nhiều điểm vàng là do hệ quả của việc uống nhiều rượu ngải cứu có xantonin (thành phần trong thuốc tẩy giun trẻ em) - khi uống phải loại rượu này, người uống sẽ nhìn thấy mọi vật xung quanh nhuốm vàng.\r\n\r\nĐêm đầy sao nằm trong bộ sưu tập của Bảo tàng Nghệ thuật Hiện đại ở thành phố New York. Mỹ.', '04c2-thestarrynight18891.jpg', '2014-12-11 04:20:16', '2014-12-11 04:20:16'),
(16, 'The Kiss', 'Gustav Klimt (Áo)', 1908, '"The Kiss" hay còn gọi là Der Kuss trong tiếng Đức hoặc Nụ hôn trong tiếng Việt, được coi là bảo vật của Anh quốc, kiệt tác của hội họa hiện đại thời kỳ đầu và là bức họa nổi tiếng nhất của danh họa Gustav Klimt. Nụ hôn tượng trưng cho khát vọng của Klimt về giải phóng bản thân và tự do tình dục. \r\n\r\nTranh miêu tả một đôi nam nữ hôn nhau, được vẽ trong sắc vàng và nhiều hình khối, với phông nền màu đồng. Ông vẽ bức tranh năm 45 tuổi, lúc sống cùng mẹ và hai chị gái độc thân, đồng thời có nhiều người tình và ít nhất 3 đứa con ngoài giá thú. \r\n\r\nHình ảnh của Gustav Klimt và bức tranh Nụ hôn đã được chọn để in lên đồng vàng 100 euro của bộ sưu tập tiền hội họa phát hành tháng 11/2003. Mặt trước đồng xu là Klimt trong xưởng vẽ với 2 bức tranh chưa hoàn thành trên giá; mặt sau in hình cặp đôi trong Nụ hôn với dòng chữ "Der Kuss".\r\n\r\nBức tranh đang được trưng bày tại bảo tàng Österreichische Galerie Belvedere ở Vienna.', '3-450pxGustavKlimt016.jpg', '2014-12-11 04:21:38', '2014-12-11 04:21:38'),
(17, 'Luncheon of the Boating Party', 'Pierre Auguste Renoir', 1881, 'Ngoài danh họa Claude Monet, trường phái ấn tượng nước Pháp nửa cuối thế kỷ XIX còn tôn vinh Pierre-Auguste Renoir. Ông là một họa sĩ luôn đề cao vẻ đẹp, đặc biệt là về vẻ đẹp cơ thể của nữ giới. \r\n\r\nLuncheon of the Boating Party (tên tiếng Pháp: Le Dejeuner des Canotiers, tạm dịch: Bữa ăn trưa của cuộc du ngoạn trên thuyền) là bức tranh sơn dầu theo trường phái ấn tượng do Pierre-Auguste Renoir vẽ vào năm 1881. Bức tranh mô tả hình ảnh một nhóm bạn của Renoir đang ăn uống, trò chuyện tại ban công nhà hàng Fournais Maison, bên dòng sông Seine, thuộc vùng Chatou, phía Tây Paris (Pháp). \r\n\r\nNhà hàng Fournais Maison là một địa điểm được họa sĩ ưa chuộng và thường chọn làm bối cảnh cho các tác phẩm của ông. Ngoài Luncheon of the Boating Party, nhà hàng Fournaise Maison còn xuất hiện trong các tác phẩm khác như Lunch at the Restaurant Fournaise (The Rowers’ Lunch).\r\n\r\nBức họa được Bảo tàng nghệ thuật Phillips ở Washington D.C (Mỹ) mua lại vào năm 1923 với giá 125.000 đô la Mỹ. Đến nay, Luncheon of the Boating Party vẫn là tác phẩm nổi tiếng nhất được lưu giữ và trưng bày tại đây.', '4-800pxDejeunercanotiers.jpg', '2014-12-11 04:22:30', '2014-12-11 04:22:30'),
(18, 'The Cafe Terrace on the Place de Forum, Arles, At Night', 'Vincent van Gogh', 1888, '“Cafe Terrace on Place du Forum, Arles, at Night” hay Cà phê về đêm trên vỉa hè khách sạn Forum, thành phố Arles (miền Nam nước Pháp) - ra đời vào giữa tháng 9/1888. \r\n\r\nNgày nay, quán cà phê và khách sạn Forum vẫn còn. Những ai yêu tranh Van Gogh vẫn thường tới ngồi cà phê ở góc này, riêng quán cà phê đã được đổi tên thành Café Van Gogh từ năm 2003.\r\n\r\nĐây là bức tranh đầu tiên Van Gogh vẽ bầu trời sao làm nền. 1 năm sau khi vẽ bức Cafe Terrace, Van Gogh vẽ tiếp bức The Starry Night. Trong một bức thư gửi em trai Theo, Van Gogh có dòng nhắc về bức Cafe Terrace rằng ông đã vẽ "cảnh của quán cà phê trên vỉa hè của khách sạn Forum, nơi bọn anh thường đến" và "vẽ lúc đêm".\r\n\r\nCafe Terrace hiện được trưng bày tại bảo tàng Kröller-Müller ở thị trấn Otterlo của Hà Lan, nơi đang sở hữu một trong những bộ sưu tập lớn nhất thế giới về các tác phẩm Van Gogh, chỉ đứng sau Bảo tàng Van Gogh ở Asterdam.', 'VanGoghTerrassedesCafesanderPlaceduForuminArlesamAbend1jpeg.jpeg', '2014-12-11 04:24:11', '2014-12-11 04:24:11'),
(19, 'Cây và những con quạ ', 'Caspar David Friedrich', 1822, 'Tranh phong cảnh "Cây và những con quạ" là bức tranh sơn dầu phong cảnh đẹp sáng tác năm 1822 bởi nghệ sĩ lãng mạn người Đức Caspar David Friedrich. Hiện nay bức tranh này hiện đang được trưng bày tại bảo tàng Louvre ở Paris. Đây là bức họa đứng đầu trong danh sách top 10 bức tranh phong cảnh đẹp nhất thế giới.', 'top-10-tranh-phong-canh-dep-nhat-1.jpg', '2014-12-11 04:30:39', '2014-12-11 04:30:39'),
(20, 'In the Mountain', 'Albert Bierstadt', 1867, 'Tranh phong cảnh "In the Mountain" (Tạm dịch: Trên núi) là một trong những bức phong cảnh sơn dầu trên vải rất nổi tiếng của họa sĩ Albert Bierstadt. Bức tranh này được hoàn thành vào năm 1867 và hiện được trưng bày tại Wadsworth Atheneum, Hartford, CT, Mỹ.', 'top-10-tranh-phong-canh-dep-nhat-2.jpg', '2014-12-11 04:31:44', '2014-12-11 04:31:44'),
(21, 'Toàn cảnh Toledo', 'Toàn cảnh', 1600, 'Toàn cảnh Toledo là một trong hai bức tranh phong cảnh còn sót lại của El Greco. Cùng với bức "Đêm đầy sao" của Vincent van Gogh, một số tranh phong cảnh của William Turner và một số tác phẩm của Monet, đây là một trong số những bức miêu tả bầu trời đẹp nhất trong nghệ thuật phương Tây với các màu sắc tương phản giữa bầu trời và các ngọn đồi ở bên dưới. Bức tranh này được thực hiện trong khoảng thời gian những năm 1596-1600 với chất liệu sơn dầu trên vải. Hiện bức tranh thuộc quyền sở hữu của Bảo tàng Mỹ thuật Metropolitian, thành phố New York, Mỹ.', 'top-10-tranh-phong-canh-dep-nhat-3.jpg', '2014-12-11 04:33:16', '2014-12-11 04:33:16'),
(22, 'Công viên Wivenhoe', 'John Constable', 1817, 'Bức tranh này được trưng bày tại  Học viện Hoàng gia Anh năm 1817 thể hiện mong muốn của Constable trở thành một họa sĩ vẽ phong cảnh tự nhiên. Bức sơn dầu trên vải này nhìn chân thực và sống động y như ảnh chụp vậy.', 'top-10-tranh-phong-canh-dep-nhat-5.jpg', '2014-12-11 04:34:22', '2014-12-11 04:34:22'),
(23, 'Cây anh túc hoang dại', 'Claude Monet', 1873, 'Bức "Wild Poppies" (Tạm dịch: Cây anh túc hoang dại) được hoàn thành năm 1873, là một trong những bức tranh phong cảnh đẹp nhất của Claude Monet. Bức tranh được thực hiện với chất liệu sơn dầu trên vải trong giai đoạn hội họa ấn tượng.', 'top-10-tranh-phong-canh-dep-nhat-6.jpg', '2014-12-11 04:35:03', '2014-12-11 04:35:03'),
(24, 'Phong cảnh Tahitian', 'Paul Gauguin', 1823, 'Với màu xanh, cam, vàng, tím rực rỡ, bức "Phong cảnh Tahitian" của Paul GauGuin đại diện cho một thiên đường của cây cọ, núi và đồng cỏ. Đằng sau bức tranh phong cảnh đẹp này là cả một câu chuyện dài về cuộc đời chiến đấu của một chiến sĩ bộ đội. Bức tranh cũng là lời gửi gắm của họa sĩ Paul GauGuin.', 'top-10-tranh-phong-canh-dep-nhat-7.jpg', '2014-12-11 04:35:50', '2014-12-11 04:35:50'),
(25, 'Dresden Neustadt', 'Oskar Kokoschka', 1919, 'Bức Dresden Neustadt là một bức tranh nổi tiếng của Oskar Kokoschka hoàn thành năm 1919. Bức tranh miêu tả khung cảnh một thành phố.', 'top-10-tranh-phong-canh-dep-nhat-7.jpg', '2014-12-11 04:36:33', '2014-12-11 04:36:33'),
(26, 'Ngôi làng bên bờ sông Seine', 'Caspar David Friedrich', 1872, 'Năm 1872, họa sĩ Alfred Sisley đã cho ra mắt tác phẩm đầy tâm huyết của mình, bức tranh phong cảnh "Ngôi làng bên bờ sông Seine". Bức tranh này làm từ chất liệu sơn dầu trên vải theo phong cách ấn tượng, hiện đang được lưu trữ tại Hermitage, St. Petersburg, Nga. Đây xứng đáng là một trong những bức tranh phong cảnh đẹp nhất thế giới.', 'top-10-tranh-phong-canh-dep-nhat-7.jpg', '2014-12-11 04:37:18', '2014-12-11 07:05:07'),
(27, 'Thác nước Bridal Veil, Yosemite', 'Albert Bierstadt', 1870, 'Bức tranh phong cảnh này được hoàn thành năm 1871, tranh sơn dầu trên vải, hiện đang được lưu trữ tại Bảo tàng Nghệ thuật Bắc Carolina, Raleigh, Mỹ.', 'top_10_tranh_phong_canh_dep_nhat__10___1_.jpg', '2014-12-11 04:38:20', '2014-12-11 04:38:20'),
(28, 'Café Terrace at Night', ' Van Gogh', 1888, 'Café Terrace at Night (Cà phê vỉa hè trong đêm ) mô tả một đêm giữa tháng chín năm 1888 tại Arles, Pháp. Vỉa hè khách sạn Forum, nơi Vincent Van Gogh ngồi vẽ đã trở thành địa điểm du lịch nổi tiếng, thu hút rất nhiều người yêu mến danh họa này.', 'Cafe-Terrace-at-Night-Vincent-Van-Gogh-1378001713.jpg', '2014-12-11 07:09:20', '2014-12-11 07:09:20'),
(29, 'The Persistence of Memory', 'Salvador Dali', 1940, 'The Persistence of Memory (Sự dai dẳng của ký ức) là tác phẩm đỉnh cao của nghệ thuật siêu thực những năm 1930–1940, nó được xem như là tư duy trừu tượng về sự sụp đổ của trật tự vũ trụ.', 'The-Persistence-of-Memory-Salvador-Dali-1378001713.jpg', '2014-12-11 07:10:33', '2014-12-11 07:10:33'),
(30, 'Portrait de l''artiste sans barbe', 'Van Gogh', 1998, 'Van Gogh vẽ rất nhiều chân dung tự họa, trong đó nổi tiếng nhất là bức tranh “Portrait de l''artiste sans Barbe”. Tác phẩm này được bán với giá 71.5 triệu USD vào năm 1998 và cũng là một trong những tác phẩm đắt giá nhất mọi thời đại.', 'Portrait-de-l-artiste-sans-barbe-Vincent-Van-Gogh-1378001713.jpg', '2014-12-11 07:11:51', '2014-12-11 07:11:51'),
(31, 'Trường học Aten ', 'Raphael', 1884, 'Bức tranh đã mô tả một thời kì hoàn kim của lịch sử văn hóa nhân loại thong qua một cuộc tranh luận về bí ẩn lịch sử vũ trụ với sự tham gia của đầy đủ các nhà triết học tư tưởng,các nhà bác học lỗi lạc thời Hi Lạp cổ\r\n\r\nHiện diện trong bức hoạ là hầu hết những bộ óc vĩ đại của nền văn minh cổ Hy lạp. Thầy trò Platon – Aristote đi sóng đôi ở giữa, góc dưới bên trái là pythago, góc bên phải là nhà thiên văn học ptoléme, ngoài cùng bên trái Archimède trần truồng lao vào “-Tìm thấy rồi! Tìm thấy rồi!”, gần bên trái là Socrates với thói quen quen thuộc tụ tập đám đông thanh niên để bàn về vấn đề triết lý', 'nhung-buc-hoa-noi-tieng-nhat-the-gioi-1.jpg', '2014-12-22 01:13:15', '2014-12-22 01:13:15'),
(32, 'Bữa ăn tối cuối cùng ( The last super )', 'Leonardo Da Vinci', 1784, 'The last super, Lại là một kiệt tác nữa của họa sĩ thiên tài Leonardo Da Vinci. Hàng trăm năm qua, biết bao người đã từng đến nhà thờ Milan để ngắm nhìn bức tranh và cũng từng ấy người đã xót xa khi nhìn thấy sự xuống cấp của nó. Chỉ có điều dù xót xa đến bao nhiêu, trái tim họ, tâm hồn họ đều chấn động ngỡ ngàng khi nhìn thấy bức tranh. Họ như sống lại với thời khắc bên buổi tiệc biệt ly của ngày thứ 5 ấy. Trong những giờ phút cuối cùng của chúa Jesu, như được sống bên cạnh các tông đồ với bao cảm xúc trái ngược khi nghe chúa nói “Một trong số những người các ngươi sẽ phản ta”.', 'nhung-buc-hoa-noi-tieng-nhat-the-gioi-4.jpg', '2014-12-22 01:14:31', '2014-12-22 01:14:31'),
(33, 'The Card Players', 'Paul Cézanne', 1893, 'Bức tranh được bán cho gia đình hoàng gia Qatar với mức giá kỷ lục hơn 250 triệu USD trong một cuộc mua bán mang tính cá nhân vào năm 2011. Với mức giá này nó trở thành tác phẩm đắt tiền nhất từng được bán từ trước đến nay. Đây cũng là phiên bản cuối cùng của nhóm các bức tranh sơn dầu cùng chủ đề của họa sĩ nổi tiếng người Pháp Paul Cézanne (1839-1906) thuộc trường phái Hậu ấn tượng. Nhóm bức tranh “The Card Players” được hoàn thành vào những năm cuối đời của vị họa sĩ với tổng cộng 5 phiên bản, mỗi phiên bản đều có sự khác biệt về kích cỡ và số lượng người chơi bài.', '1-82bcd.jpg', '2014-12-22 01:16:35', '2014-12-22 01:16:35'),
(34, 'Bức “Flag”', 'Jasper Johns', 1958, 'Bức tranh là tác phẩm nổi tiếng nhất của họa sĩ người Mỹ Jasper Johns (sn 1930) được sáng tác sau khi ông có một giấc mơ về quốc kỳ của đất nước mình vào năm 1954. Vào năm 2010, bức tranh đã được tỷ phú Steven Cohen đưa về bộ sưu tập của mình với mức giá 110 triệu USD.', '7-82bcd.jpg', '2014-12-22 01:17:33', '2014-12-22 01:17:33'),
(35, 'Tác phẩm Đầu con hươu', 'Velázquez ', 1627, 'Tác phẩm Đầu con hươu được ông vẽ vào năm (1626-1627) thể hiện một linh hồn con vật đầy lòng trách ẩn. Velázquez đã thấy được chiều sâu trong đôi mắt đó.', 'head-of-a-stag-1634_jvgd.jpg', '2014-12-22 01:22:45', '2014-12-22 01:22:45'),
(36, 'Bức tranh Rideau, Cruchon et Compotier', 'Paul Cézanne', 1894, 'Paul Cézanne là một họa sĩ người Pháp thuộc trường pháo Hậu ấn tượng ông là người được cho là cây cầu nối giữa trường phái ấn tượng thế kỷ 19 tới trường phái lập thể thế kỷ 20. Các tác phẩm của Cézanne thể hiện sự sắc sảo trong thiết kế, màu sắc, pha trộn. Những nét vẽ tìm tòi, nhạy cảm của ông mang tính đặc trưng và rất dễ nhận biết. Bức tranh tĩnh vật nổi tiếng Rideau, Cruchon et Compotier còn có tên là “Những quả táo to” được ông thực hiện trong khoảng thời gian từ năm 1893 tới 1894. Nhà đấu giá nổi tiếng của Sotheby đã bán nó tại New York cuối thập kỉ 90 với giá hơn 60.000.000 đô la cho một nhà đấu giá nổi tiếng khác – The Whitney.', '8-15129.jpg', '2014-12-22 01:24:13', '2014-12-22 01:24:13'),
(37, 'Bức tranh “Vụ thảm sát người vô tội”', 'Rubens', 1611, 'Peter Paul Rubens là một họa sĩ lỗi lạc người Vlaanderen vào thế kỉ 17, ông là nhân vật khai xướng cho phong cách baroque hoa mỹ, một phong cách nhấn mạnh đến sự di chuyển, màu sắc và nhục dục. Rubens được biết đến với các tác phẩm với chủ đề về lịch sử, phong cảnh, chân dung. Bức tranh nổi tiếng nhất của ông là “Vụ thảm sát người vô tội” được vẽ vào năm 1611. Đây được đánh giá là một trong 10 kiệt tác trong lịch sử hội họa. Nhà đấu giá danh tiếng Sotheby đã bán tác phẩm nghệ thuật này cho Kenneth Thomson với cái giá hơn 76.000.000 đô la vào năm 2002', '5-15129.jpg', '2014-12-22 01:25:01', '2014-12-22 01:25:01'),
(38, 'Bức tranh “Le bassin aux nymphéas”', 'Monet', 1919, 'Họa sĩ Claude Monet một trong những người sáng lập trường phái ấn tượng trong hội họa. Ông là một người Pháp và là tác giả của nhiều bức tranh phong cảnh ấn tượng. Một trong số đó là bức “Le basin Aux nymphéas” hay còn gọi là bức “Ao súng”. Bức tranh được ông  vẽ vào năm 1919 tại Giverny. Trong vòng hơn 80 năm qua, bức tranh mới chỉ được trưng bày trước công chúng đúng một lần. Nhà đấu giá Christie đã bán nó bốn năm trước đây với giá là hơn 80.400.000 đô la. Người mua bức tranh này là một người ẩn danh.', '3-15129.jpg', '2014-12-22 01:26:54', '2014-12-22 01:26:54');

